import React,{useState} from 'react';
import MySecondHook from './secondHook';
import Blacknwhite from "../images/Blacknwhite.jpeg";
import { useHistory } from 'react-router-dom';
export default function MyFirstHook() {

    let history=useHistory();
    const [counter,setCounter]=useState(0);
    const [isLightOn,setIsLightOn]=useState(false);
    const [imageUrl,setImageUrl]=useState('https://media.istockphoto.com/photos/light-bulb-on-a-white-background-picture-id907928762');

    function handleLight() {
        if(isLightOn){
            setImageUrl('https://media.istockphoto.com/photos/light-bulb-on-a-white-background-picture-id907928762')
        }else{
            setImageUrl('data:image/jpeg;base64,')
        }
        setIsLightOn(!isLightOn);
    }

    return(
        <div>
            <div>Counter value {counter}</div>
            <button onClick={()=>setCounter(counter+1)}>Increase </button>
            <img style={{width:'30%'}} 
                onClick={()=>handleLight()}
            src={imageUrl}/>
            <button onClick={()=>history.push('/third/t/edit',{name:"Tamang"})}>Change route</button>
            <MySecondHook imageUrl={imageUrl} name="This is Joshua" localImage={Blacknwhite}/>
        </div>
    )
}